sys_script_client.script:
function onLoad() {
	setTimeout ( "var refocus = g_form.getElement('sys_display.x_skatt_a_post_posthantering.u_mottagare');refocus.focus();" , 0 ) ;
	var valdReception = g_form.getLabelOf(u_reception, g_form.getValue('u_reception'));
	var valdStatus = g_form.getLabelOf(u_status, g_form.getValue('u_status'));
	g_form.clearOptions('u_reception');

	g_form.clearOptions('u_status');
	g_form.addOption('u_status','Nyinkommet','Nyinkommet');
	g_form.addOption('u_status','Påminnelse 1','Påminnelse_1');
	g_form.addOption('u_status','Påminnelse 2','Påminnelse_2');
	g_form.addOption('u_status','Receptionen','Receptionen');
	g_form.addOption('u_status','Hämtat','Hämtat');

	//Type appropriate comment here, and begin script below
	var prenot = new GlideAjax('Post_GetReceptionList');
	prenot.addParam('sysparm_name','getReceptions');
	prenot.getXML(clientFunction);


	if(!g_form.isNewRecord()){
		g_form.setValue('u_reception', valdReception);
		g_form.setValue('u_status', valdStatus);		
		}

	function clientFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var receptioner= [];
		var array = [];
		receptioner = JSON.parse(answer);
		for (var i=0; i < receptioner.length; i++) {
			array = receptioner[i].split("|");
			g_form.addOption('u_reception',array[1],array[0]);
		}
	} // END function clientFunction(response) {

	} //END function onLoad() {
